import brain from "brain";
import type React from "react";
import { useEffect, useState } from "react";

const sleep = (seconds: number): Promise<void> => {
  return new Promise((resolve) => setTimeout(resolve, seconds * 1000));
};

export type Props = {};

const App: React.FC = (props: Props) => {
  const [fjongResponse, setFjongResponse] = useState("");

  const fetchFjongResponse = async () => {
    try {
      const response = await brain.get_joke();
      const data = await response.json();
      setFjongResponse(data.joke);
    } catch (error) {
      console.error("Error fetching fjong response:", error);
    }
  };

  useEffect(() => {
    fetchFjongResponse();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-4xl font-bold mb-4">Make me laugh!</h1>
      <textarea
        className="w-full h-64 p-4 border rounded-lg bg-blue-100"
        value={fjongResponse}
        readOnly
      />
      <button
        className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-700"
        onClick={fetchFjongResponse}
      >
        Refresh Joke
      </button>
      <p className="mt-4 text-black text-green-500">
        Warning: These jokes may be offensively bad.
      </p>
    </div>
  );
};

export default App;
